import fetch from 'node-fetch';

export const authenticate = (username, password) => {
    const response = fetch("https://api.kvikmyndir.is/authenticate", {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            username: username,
            password: password
        })
    });
    return response.data;
}











