import React from "react";
import { View, Text, Button } from "native-base";
import { useSelector, useDispatch } from "react-redux";
import store from "../../DataSystem/Redux/store";
import { useNavigation } from "@react-navigation/native";



const LoadPage = () => {

    // a function that checks if the AllDataLoaded.loaded is true
    // if it is true, then it will navigate to the home page
    // if it is false, then it will stay on the load page

    const AllDataLoaded = useSelector(state => state.AllDataLoaded);
    const { navigate } = useNavigation();

    const checkLoading = () => {
        if (AllDataLoaded.loaded === true) 
        {
            return navigate("Home")
        }
    }

    return checkLoading();




    }

export default LoadPage
