import React from "react";
import { View, Text, Button } from "native-base";
import { useSelector, useDispatch } from "react-redux";
import store from "../../DataSystem/Redux/store";
import { useNavigation } from "@react-navigation/native";



const LoadPage = () => {

        const {navigate} = useNavigation();

        // a function that check whether the DataSystem is loaded or not
        const checkDataSystem = () => {
            // if the DataSystem is loaded, navigate to Home
            if (store.getState().DataSystem.isLoaded) {
                navigate("Home")
            }
            // if the DataSystem is not loaded, wait for 1 second and check again
            else {
                setTimeout(() => {
                    checkDataSystem()
                }, 1000)
            }
        }




        return (
            <View>
            <Text>LoadPage</Text>
            <Button title="Test" onPress={() => navigate("Home")}/>
            </View>
        )
    }

export default LoadPage
