import axios from 'axios';

// function to resolve promise
function resolvePromise(response) {
    const retval = Promise.resolve(response);
    return retval.data;
}


async function authenticate(username, password) {
     const response = await axios.post("https://api.kvikmyndir.is/authenticate", {
        username: username,
        password: password
    }).then(resolvePromise(response));
    return response;
}

async function getMovies (token) {
    return axios.get("https://api.kvikmyndir.is/movies?token=" + token, {
    });
    }

async function getCinemas (token) {
    return axios.get("https://api.kvikmyndir.is/cinemas?token=" + token, {
    });
    }

async function getGenres (token) {
    return axios.get("https://api.kvikmyndir.is/genres?token=" + token, {
    });
    }

async function getUpcomingMovies (token) {
    return axios.get("https://api.kvikmyndir.is/upcoming?token=" + token, {
    });
    }


export { authenticate, getMovies, getCinemas, getGenres, getUpcomingMovies };
