import axios from "axios";


export const authenticate = (username, password) => {
    console.log("Authenticating");
    const response = axios.post("https://api.kvikmyndir.is/authenticate", {
        username,
        password
    });
    
    return response.Promise
    }

export const getMovies = (token) => {
    return axios.get("https://api.kvikmyndir.is/movies?token=" + token, {
    });
    }

export const getCinemas = (token) => {
    return axios.get("https://api.kvikmyndir.is/cinemas?token=" + token, {
    });
    }

export const getGenres = (token) => {
    return axios.get("https://api.kvikmyndir.is/genres?token=" + token, {
    });
    }

export const getUpcomingMovies = (token) => {
    return axios.get("https://api.kvikmyndir.is/upcoming?token=" + token, {
    });
    }
