import React from 'react';
import * as axios from 'axios';
import * as thunk from 'redux-thunk';
import * as redux from 'redux';
import { createStore, applyMiddleware } from 'redux';





const baseUrl = 'https://api.kvikmyndir.is/'

const initialState = {
    loading: false,
    authenticate: [],
    movies: [],
    cinemas: [],
    genres: [],
    upcoming: [],
    error: '',
}

const FETCH_AUTHENTICATE_REQUEST = 'FETCH_AUTHENTICATE_REQUEST'
const FETCH_AUTHENTICATE_SUCCESS = 'FETCH_AUTHENTICATE_SUCCESS'
const FETCH_AUTHENTICATE_FAILURE = 'FETCH_AUTHENTICATE_FAILURE'

const FETCH_MOVIES_REQUEST = 'FETCH_MOVIES_REQUEST'
const FETCH_MOVIES_SUCCESS = 'FETCH_MOVIES_SUCCESS'
const FETCH_MOVIES_FAILURE = 'FETCH_MOVIES_FAILURE'

const FETCH_CINEMAS_REQUEST = 'FETCH_CINEMAS_REQUEST'
const FETCH_CINEMAS_SUCCESS = 'FETCH_CINEMAS_SUCCESS'
const FETCH_CINEMAS_FAILURE = 'FETCH_CINEMAS_FAILURE'

const FETCH_GENRES_REQUEST = 'FETCH_GENRES_REQUEST'
const FETCH_GENRES_SUCCESS = 'FETCH_GENRES_SUCCESS'
const FETCH_GENRES_FAILURE = 'FETCH_GENRES_FAILURE'

const FETCH_UPCOMING_REQUEST = 'FETCH_UPCOMING_REQUEST'
const FETCH_UPCOMING_SUCCESS = 'FETCH_UPCOMING_SUCCESS'
const FETCH_UPCOMING_FAILURE = 'FETCH_UPCOMING_FAILURE'

const fetchAuthenticateRequest = () => {
    return {
        type: FETCH_AUTHENTICATE_REQUEST
    }
}

const fetchAuthenticateSuccess = authenticate => {
    return {
        type: FETCH_AUTHENTICATE_SUCCESS,
        payload: authenticate
    }
}

const fetchAuthenticateFailure = error => {
    return {
        type: FETCH_AUTHENTICATE_FAILURE,
        payload: error
    }
}

const fetchMoviesRequest = () => {
    return {
        type: FETCH_MOVIES_REQUEST
    }
}

const fetchMoviesSuccess = movies => {
    return {
        type: FETCH_MOVIES_SUCCESS,
        payload: movies
    }
}

const fetchMoviesFailure = error => {
    return {
        type: FETCH_MOVIES_FAILURE,
        payload: error
    }
}

const fetchCinemasRequest = () => {
    return {
        type: FETCH_CINEMAS_REQUEST
    }
}

const fetchCinemasSuccess = cinemas => {
    return {
        type: FETCH_CINEMAS_SUCCESS,
        payload: cinemas
    }
}

const fetchCinemasFailure = error => {
    return {
        type: FETCH_CINEMAS_FAILURE,
        payload: error
    }
}

const fetchGenresRequest = () => {
    return {
        type: FETCH_GENRES_REQUEST
    }
}

const fetchGenresSuccess = genres => {
    return {
        type: FETCH_GENRES_SUCCESS,
        payload: genres
    }
}

const fetchGenresFailure = error => {
    return {
        type: FETCH_GENRES_FAILURE,
        payload: error
    }
}

const fetchUpcomingRequest = () => {
    return {
        type: FETCH_UPCOMING_REQUEST
    }
}

const fetchUpcomingSuccess = upcoming => {
    return {
        type: FETCH_UPCOMING_SUCCESS,
        payload: upcoming
    }
}

const fetchUpcomingFailure = error => {
    return {
        type: FETCH_UPCOMING_FAILURE,
        payload: error
    }
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case FETCH_AUTHENTICATE_REQUEST:
            return {
                ...state,
                loading: true
            }
        case FETCH_AUTHENTICATE_SUCCESS:
            return {
                loading: false,
                authenticate: action.payload,
                movies: state.movies,
                cinemas: state.cinemas,
                genres: state.genres,
                upcoming: state.upcoming,
                error: ''
            }
        case FETCH_AUTHENTICATE_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case FETCH_MOVIES_REQUEST:
            return {
                ...state,
                loading: true
            }
        case FETCH_MOVIES_SUCCESS:
            return {
                loading: false,
                movies: action.payload,
                error: ''
            }
        case FETCH_MOVIES_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case FETCH_CINEMAS_REQUEST:
            return {
                ...state,
                loading: true
            }
        case FETCH_CINEMAS_SUCCESS:
            return {
                loading: false,
                cinemas: action.payload,
                error: ''
            }
        case FETCH_CINEMAS_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case FETCH_GENRES_REQUEST:
            return {
                ...state,
                loading: true
            }
        case FETCH_GENRES_SUCCESS:
            return {
                loading: false,
                genres: action.payload,
                error: ''
            }
        case FETCH_GENRES_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case FETCH_UPCOMING_REQUEST:
            return {
                ...state,
                loading: true
            }
        case FETCH_UPCOMING_SUCCESS:
            return {
                loading: false,
                upcoming: action.payload,
                error: ''
            }
        case FETCH_UPCOMING_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        default: return state
    }
}

const fetchAuthenticate = async () => {
    return function(dispatch) {
        dispatch(fetchAuthenticateRequest())
        axios
        .post(baseUrl + 'authenticate', {
            username: "hopur26v2",
            password: "hopur26"
        }  )
        .then(response => {
            const authenticate = response.data
            dispatch(fetchAuthenticateSuccess(authenticate))
        })
        .catch(error => {
            const errorMsg = error.message
            console.log(errorMsg)
            dispatch(fetchAuthenticateFailure(errorMsg))
        })
    }
}

const fetchMovies = async () => {
    return function(dispatch) {
        dispatch(fetchMoviesRequest())
        axios
        .get(baseUrl + 'movies?token=' + store.authenticate.token)
        .then(response => {
            const movies = response.data
            dispatch(fetchMoviesSuccess(movies))
        })
        .catch(error => {
            const errorMsg = error.message
            console.log(errorMsg)
            dispatch(fetchMoviesFailure(errorMsg))
        })
    }
}

const fetchCinemas = async () => {
    return function(dispatch) {
        dispatch(fetchCinemasRequest())
        axios
        .get(baseUrl + 'cinemas?token=' + store.authenticate.token)
        .then(response => {
            const cinemas = response.data
            dispatch(fetchCinemasSuccess(cinemas))
        })
        .catch(error => {
            const errorMsg = error.message
            console.log(errorMsg)
            dispatch(fetchCinemasFailure(errorMsg))
        })
    }
}

const fetchGenres = async () => {
    return function(dispatch) {
        dispatch(fetchGenresRequest())
        axios
        .get(baseUrl + 'genres?token=' + store.authenticate.token)
        .then(response => {
            const genres = response.data
            dispatch(fetchGenresSuccess(genres))
        })
        .catch(error => {
            const errorMsg = error.message
            console.log(errorMsg)
            dispatch(fetchGenresFailure(errorMsg))
        })
    }
}

const fetchUpcoming = async () => {
    return function(dispatch) {
        dispatch(fetchUpcomingRequest())
        axios
        .get(baseUrl + 'upcoming?token=' + store.authenticate.token)
        .then(response => {
            const upcoming = response.data
            dispatch(fetchUpcomingSuccess(upcoming))
        })
        .catch(error => {
            const errorMsg = error.message
            console.log(errorMsg)
            dispatch(fetchUpcomingFailure(errorMsg))
        })
    }
}




const store = redux.createStore(reducer, redux.applyMiddleware(thunk))



store.subscribe(() => {console.log(store.getState())})
store.dispatch(fetchAuthenticate())
//store.dispatch(fetchMovies())

