import React from "react";
import { View, Text, Button, Center,Img } from "native-base";
import { useSelector, useDispatch } from "react-redux";
import store from "../../DataSystem/Redux/store";
import { useNavigation } from "@react-navigation/native";
import { SafeAreaView } from "react-native";



const LoadPage = () => {
    
    const AllDataLoaded = useSelector(state => state.AllDataLoaded);
    const allMovies = useSelector(state => state.fetchMovies);
    const { navigate } = useNavigation();

    const checkLoading = () => {
        console.log(allMovies)
        if (AllDataLoaded.loaded === true) 
        {
            //wait for half a second
            setTimeout(() => {
                navigate("Home");
            }, 5000);  
        }

    }

    //checkLoading();

    return (
        <SafeAreaView>
        <Center>
        // display gif from url 
        <Img source={{uri: 'https://media.giphy.com/media/3oEjI6SIIHBdRxXI40/giphy.gif'}} alt="Loading" />
        <Text>Loading...</Text>
        </Center>
        <Text>LoadPage</Text>
        <Text>{AllDataLoaded.loaded}</Text>
        <Button title="Test" onPress={checkLoading()}/>
        </SafeAreaView>
    )

        
    }

export default LoadPage
